mapper.select:
		select count(1) from tran_discount

		1=1


        	 and begin_date = #{beginDate,jdbcType=DATE}




        	 and end_date = #{endDate,jdbcType=DATE}



           	and week = #{week}


           	 and begin_time = #{beginTime}


           	 and end_time = #{endTime}


           	 and discount_threshold = #{discountThreshold}


           	 and set_discount = #{setDiscount}

mapper.insert:
INSERT INTO TRAN_DISCOUNT(ID,BEGIN_DATE,END_DATE,WEEK,BEGIN_TIME,END_TIME,DISCOUNT_THRESHOLD,SET_DISCOUNT,OLD_SALE_ID) select tran_discount_id_seq.NEXTVAL,a.* from(
select #{item.beginDate}, #{item.endDate}, #{item.week}, #{item.beginTime}, #{item.endTime}, #{item.discountThreshold}, #{item.setDiscount}, #{item.oldSaleId} from dual
) a
